#include "LIBBSP_MATH_Casts.h"

 void LIBBSP_MATH_ConvertFloat64ToInt64 ( const float64_t         v_Value_Float64,                                             int64_t  * const  pa_Value_Int64) {    float64_t v_MSB;    float64_t v_LSB;    boolean_t v_IsNegative;    int64_t v_MSBInt;    uint64_t v_LSBInt;    int64_t v_tempInt64;    float64_t v_inputData;    if (v_Value_Float64 < 0.0)    {       v_IsNegative = TRUE;       v_inputData = -v_Value_Float64;    }    else    {       v_IsNegative = FALSE;       v_inputData = v_Value_Float64;    }    v_MSB = v_inputData / C_LIBMATH_CONST_FL64_2EXP32;    v_LSB = v_inputData - (((float64_t)((int32_t)v_MSB)) * C_LIBMATH_CONST_FL64_2EXP32);    v_MSBInt = (int64_t)((int32_t)v_MSB);    v_LSBInt = (uint64_t)((uint32_t)v_LSB);    v_tempInt64 = (int64_t)(v_MSBInt << C_LIBMATH_CONST_NB_BIT_WORD32) + (int64_t)v_LSBInt;    if (v_IsNegative)    {       v_tempInt64 = -v_tempInt64;    }    *pa_Value_Int64 = v_tempInt64; }
