#include "LIBUTI_COMMON.h"
#include "LIBUTI_CMN_MatchingFiles.h"

     for(index=((uint32_t) 0);index<NumberOfDataFiles;index++)     {         DataFileFound = FALSE;         for(index_bis=((uint32_t) 0);index_bis<NumberOfDataFiles;index_bis++)         { 			LIBUTI_CMN_MatchingFiles(DataFilesInfo[index].ARINC_665_FileInfo->Name, DataFilesInfo[index].ARINC_665_FileInfo->NameLength, SUP_LUP_InfoIterator[index_bis].LUP_Name, &MatchingFileName);             if(MatchingFileName)             {                 DataFileFound = TRUE;                 DataFilesInfo[index].Address                     = SUP_LUP_InfoIterator[index_bis].LUP_DestinationAddress;                 if (SUP_LUP_InfoIterator[index_bis].LUP_EncryptionStatus == (uint16_t)C_ENCRYPTED)                 {                     DataFilesInfo[index].Encrypted = TRUE;                 }                 else                 {                     DataFilesInfo[index].Encrypted = FALSE;                 }                 DataFilesInfo[index].ExpectedCRC16                     = SUP_LUP_InfoIterator[index_bis].LUP_EncryptedCRC16;                 if(SUP_LUP_InfoIterator[index_bis].LUP_Type == (uint16_t)C_MAIN_APPLICATION_FILE)                 {                     if(*LaunchAddressFound == FALSE)                     {                         *LaunchAddress = (uint8_t*)(((uint32_t)SUP_LUP_InfoIterator[index_bis].LUP_DestinationAddress)                              + SUP_LUP_InfoIterator[index_bis].LUP_EntryPointDelta);                         *LaunchAddressFound = TRUE;                     }                     else                     {                         *OutOfOperationalConditionSUP = TRUE;                         return;                     }                 }                 break;             }         }         if(DataFileFound == FALSE)         {             *OutOfOperationalConditionSUP = TRUE;             return;         }     }
