#include "LIBUTI_SHA.h"
#include "LIBUTI_MEM.h"
#include "LIBUTI_SHA_Transform.h"

 void LIBUTI_SHA_finalSHA256(ts_LIBUTI_SHA_SHA256_ctx * const Ctx, ts_LIBUTI_SHA_SHA256 * const ComputedHash) {    uint32_t i;    i = Ctx->Datalen;    if (Ctx->Datalen < (uint32_t)56)     {        Ctx->Data[i] = (uint8_t)0x80;        i++;        while (i < (uint32_t)56)        {            Ctx->Data[i] = (uint8_t)0x00;            i++;        }    }    else     {        Ctx->Data[i] = (uint8_t)0x80;        i++;        while (i < (uint32_t)64)        {            Ctx->Data[i] = (uint8_t)0x00;            i++;        }        SHA_sha256_transform(Ctx, Ctx->Data);        LIBUTI_MEM_MemSet(Ctx->Data, (uint8_t)0, (uint32_t)56);    }    Ctx->Bitlen += (uint64_t)Ctx->Datalen * (uint64_t)8;    Ctx->Data[63] = (uint8_t) (Ctx->Bitlen);    Ctx->Data[62] = (uint8_t) (Ctx->Bitlen >> 8);    Ctx->Data[61] = (uint8_t) (Ctx->Bitlen >> 16);    Ctx->Data[60] = (uint8_t) (Ctx->Bitlen >> 24);    Ctx->Data[59] = (uint8_t) (Ctx->Bitlen >> 32);    Ctx->Data[58] = (uint8_t) (Ctx->Bitlen >> 40);    Ctx->Data[57] = (uint8_t) (Ctx->Bitlen >> 48);    Ctx->Data[56] = (uint8_t) (Ctx->Bitlen >> 56);    SHA_sha256_transform(Ctx, Ctx->Data);    for (i = (uint32_t)0; i < (uint32_t)4; ++i) {        ComputedHash->Data[i]                = (uint8_t) ( (Ctx->State[0] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );        ComputedHash->Data[i + (uint32_t)4]  = (uint8_t) ( (Ctx->State[1] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );        ComputedHash->Data[i + (uint32_t)8]  = (uint8_t) ( (Ctx->State[2] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );        ComputedHash->Data[i + (uint32_t)12] = (uint8_t) ( (Ctx->State[3] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );        ComputedHash->Data[i + (uint32_t)16] = (uint8_t) ( (Ctx->State[4] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );        ComputedHash->Data[i + (uint32_t)20] = (uint8_t) ( (Ctx->State[5] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );        ComputedHash->Data[i + (uint32_t)24] = (uint8_t) ( (Ctx->State[6] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );        ComputedHash->Data[i + (uint32_t)28] = (uint8_t) ( (Ctx->State[7] >> ((uint32_t)24 - (i * (uint32_t)8))) & (uint32_t)0x000000ff );    } }
