#include "middleware/t4/t4_private.h"

    if (type == e_interp_volt_to_degree)    {       if (input_value <= T4_VOLT_VAL_MIN)       {          *output_value = T4_DEG_VAL_MIN;       }       else if (input_value >= T4_VOLT_VAL_MAX)       {          *output_value = T4_DEG_VAL_MAX;       }       else       {          while (found == FALSE)          {             i = (low + high) / (uint32_t)2;             if((t4_interp_table[i] <= input_value) && (t4_interp_table[i+(uint32_t)1] > input_value))                {                   *output_value = ((input_value - t4_interp_table[i]) / (t4_interp_table[i+(uint32_t)1] -                   t4_interp_table[i])) + ((float32_t)i + T4_DEG_VAL_MIN);                   found = TRUE;                }             else if (input_value > t4_interp_table[i])             {                low = i + (uint32_t)1;             }             else if (input_value < t4_interp_table[i])             {                high = i - (uint32_t)1;             }          }       }    }
