#include "BOOT_MC_REG_public.h"
#include "BOOT_BIST_public.h"
#include "BOOT_PINOUT_public.h"
#include "BOOT_PBRIDGE_public.h"
#include "BOOT_PLL_REG_public.h"
#include "BOOT_MEM_public.h"

 void BOOT_MC_BIST_MODE_CHANGE (void) {     ts_MC_ME_REG volatile*  pt_mc_me_reg  = (ts_MC_ME_REG volatile*) MC_ME_BASE_ADDR;     ts_SIUL2_tag volatile*  pt_gpio_reg   = (ts_SIUL2_tag volatile*) SIUL2_BASE_ADDR;     ts_MC_CGM_REG volatile* pt_mc_cgm_reg = (ts_MC_CGM_REG volatile*) MC_CGM_BASE_ADDR;     ts_PLLDIG_REG volatile* pt_plldig_reg = (ts_PLLDIG_REG volatile*) PLLDIG_BASE_ADDR;     pt_mc_me_reg->s_MC_ME_RUN_PC[0]=0;           pt_mc_me_reg->s_MC_ME_RUN_PC[1]=0;           pt_mc_me_reg->s_MC_ME_RUN_PC[2]=0;           pt_mc_me_reg->s_MC_ME_RUN_PC[3]=0;           pt_mc_me_reg->s_MC_ME_RUN_PC[4]=0;           pt_mc_me_reg->s_MC_ME_RUN_PC[5]=0;           pt_mc_me_reg->s_MC_ME_RUN_PC[6]=0;           pt_mc_me_reg->s_MC_ME_RUN_PC[7]=0;           pt_mc_me_reg->s_MC_ME_DRUN_MC = 0x30130031;     pt_mc_me_reg->s_MC_ME_MCTL = 0x30005AF0;                         pt_mc_me_reg->s_MC_ME_MCTL = 0x3000A50F;                         while ( (( pt_mc_me_reg->s_MC_ME_GS & (reg32_t)0x08000000UL ) >> 27ul) == e_MC_ME_GS_MTRANS_ON_GOING );      while ( (( pt_mc_me_reg->s_MC_ME_GS & (reg32_t)0xF0000000UL ) >> 28ul) != e_MC_ME_MODE_DRUN );       pt_plldig_reg->s_PLLDIG_PLL0DV = 0x00032030;     pt_plldig_reg->s_PLLDIG_PLL1DV =0x0030030;     pt_mc_me_reg->s_MC_ME_DRUN_MC = 0x301300F4;     pt_mc_me_reg->s_MC_ME_MCTL = 0x30005AF0;                         pt_mc_me_reg->s_MC_ME_MCTL = 0x3000A50F;                         while ( (( pt_mc_me_reg->s_MC_ME_GS & (reg32_t)0x08000000UL ) >> 27ul) == e_MC_ME_GS_MTRANS_ON_GOING );      while ( (( pt_mc_me_reg->s_MC_ME_GS & (reg32_t)0xF0000000UL ) >> 28ul) != e_MC_ME_MODE_DRUN );       if(pt_gpio_reg->s_MIDR1 & 0x10){        pt_mc_cgm_reg->s_MC_CGM_SC_DIV_RC = 0x00000001;            pt_mc_cgm_reg->s_MC_CGM_DIV_UPD = 0x80000000;         }     pt_mc_cgm_reg->s_MC_CGM_SC_DC2=0x80030000;               pt_mc_cgm_reg->s_MC_CGM_SC_DC1=0x80010000;            pt_mc_cgm_reg->s_MC_CGM_SC_DC0=0x80000000;            pt_mc_cgm_reg->s_MC_CGM_SC_DC3=0x80000000;            pt_mc_cgm_reg->s_MC_CGM_SC_DC4=0x80000000;              if(pt_gpio_reg->s_MIDR1 & 0x10){    pt_mc_cgm_reg->s_MC_CGM_DIV_UPD_TRIG = 0xfeedface;           while (pt_mc_cgm_reg->s_MC_CGM_DIV_UPD_STAT == 0x10000000);        }     pt_mc_cgm_reg->s_MC_CGM_AC0_DC1=0;         pt_mc_cgm_reg->s_MC_CGM_AC0_DC2=0;         pt_mc_cgm_reg->s_MC_CGM_AC0_DC3=0;         pt_mc_cgm_reg->s_MC_CGM_AC0_DC4=0;         pt_mc_cgm_reg->s_MC_CGM_AC2_DC0=0;         pt_mc_cgm_reg->s_MC_CGM_AC2_DC1=0;         pt_mc_cgm_reg->s_MC_CGM_AC5_DC0=0;         pt_mc_cgm_reg->s_MC_CGM_AC5_DC1=0;         pt_mc_cgm_reg->s_MC_CGM_AC5_DC2=0;         pt_mc_cgm_reg->s_MC_CGM_AC6_SC=0;            pt_mc_cgm_reg->s_MC_CGM_AC7_SC=0;            pt_mc_cgm_reg->s_MC_CGM_AC8_DC0=0;         pt_mc_cgm_reg->s_MC_CGM_AC9_DC0=0;         pt_mc_cgm_reg->s_MC_CGM_AC10_DC0=0;    }
