#include "BOOT_MC_public.h"
#include "BOOT_MC_REG_public.h"

 void BOOT_MC_SET_PLL( void ) {    reg32_t * pt_mc_me_mode_register;    uint32_t v_mode_config;    te_MC_ME_MODE v_mode_select;    te_MC_ME_MODE v_current_mode;    te_MC_ME_GS_MTRANS v_mtrans_status;    te_MC_ME_GS_CLK_STABLE v_clk_status;    v_mode_config = MC_ME_DRUN_MC_CONF;    v_mode_select = e_MC_ME_MODE_DRUN;    pt_mc_me_mode_register = &v_pt_mc_me_regs->s_MC_ME_DRUN_MC;    *pt_mc_me_mode_register = ( (*pt_mc_me_mode_register) & (reg32_t)MC_ME_MODE_MC_MSK ) | (reg32_t)v_mode_config;    v_pt_mc_me_regs->s_MC_ME_MCTL = (reg32_t)( v_mode_select << 28UL ) | (reg32_t)MC_ME_MCTL_KEY;    v_pt_mc_me_regs->s_MC_ME_MCTL = (reg32_t)( v_mode_select << 28UL ) | (reg32_t)MC_ME_MCTL_INV_KEY;    do    {       v_mtrans_status = (te_MC_ME_GS_MTRANS)(( v_pt_mc_me_regs->s_MC_ME_GS & (reg32_t)0x08000000UL ) >> 27ul);    }    while ( v_mtrans_status == e_MC_ME_GS_MTRANS_ON_GOING );    do    {       v_current_mode = (te_MC_ME_MODE)(( v_pt_mc_me_regs->s_MC_ME_GS & (reg32_t)0xF0000000UL ) >> 28ul);    }    while ( v_current_mode != e_MC_ME_MODE_DRUN );    do    {       v_clk_status = (te_MC_ME_GS_CLK_STABLE)(( v_pt_mc_me_regs->s_MC_ME_GS & (reg32_t)0x00000040UL ) >> 6ul);    }    while ( v_clk_status == e_MC_ME_GS_CLK_NOT_STABLE );    do    {       v_clk_status = (te_MC_ME_GS_CLK_STABLE)(( v_pt_mc_me_regs->s_MC_ME_GS & (reg32_t)0x00000080UL ) >> 7ul);    }    while ( v_clk_status == e_MC_ME_GS_CLK_NOT_STABLE ); }
