#include "LIBUTI_COMMON.h"
#include "LIBUTI_CMN_MatchingFiles.h"

    for ( index = (uint32_t)0; index < NumberOfDataFiles; index++ )    {       DataFileFound = e_CMN_FLAG_NO;       for ( index_bis = (uint32_t)0; index_bis < NumberOfDataFiles; index_bis++ )       {          LIBUTI_CMN_MatchingFiles(DataFilesInfo[index].ARINC_665_FileInfo->Name, DataFilesInfo[index].ARINC_665_FileInfo->NameLength, SUP_LUP_InfoIterator[index_bis].LUP_Name, &MatchingFileName);          if ( MatchingFileName == e_CMN_FLAG_YES )          {             DataFileFound = e_CMN_FLAG_YES;             DataFilesInfo[index].Address = SUP_LUP_InfoIterator[index_bis].LUP_DestinationAddress;             if ( SUP_LUP_InfoIterator[index_bis].LUP_EncryptionStatus == K_ENCRYPTED )             {                DataFilesInfo[index].Encrypted = e_CMN_FLAG_YES;             }             else             {                DataFilesInfo[index].Encrypted = e_CMN_FLAG_NO;             }             DataFilesInfo[index].ExpectedCRC16                     = SUP_LUP_InfoIterator[index_bis].LUP_EncryptedCRC16;             if ( SUP_LUP_InfoIterator[index_bis].LUP_Type == K_MAIN_APPLICATION_FILE )             {                if ( *LaunchAddressFound == e_CMN_FLAG_NO )                {                   *LaunchAddress = (uint8_t*)(((uint32_t)SUP_LUP_InfoIterator[index_bis].LUP_DestinationAddress)                                   + SUP_LUP_InfoIterator[index_bis].LUP_EntryPointDelta );                   *LaunchAddressFound = e_CMN_FLAG_YES;                }                else                {                   *OutOfOperationalConditionSUP = e_CMN_FLAG_YES;                   return;                }             }             break;          }       }       if ( DataFileFound == e_CMN_FLAG_NO )       {          *OutOfOperationalConditionSUP = e_CMN_FLAG_YES;          return;       }    }
