#include "LIBUTI_SIF.h"
#include "LIBUTI_SIF_getters.h"
#include "LIBUTI_SIF_iterators.h"
#include "LIBUTI_SIF_hashes.h"
#include "LIBUTI_SIF_getters_from_headers.h"
#include "BOOT_LIBMEM_public.h"

 te_CMN_FLAG_QUESTION LIBUTI_SIF_has_target( const uint8_t * DecodedHeaderAddress, const uint8_t * TargetName, const uint32_t TargetNameLength,                                              const uint8_t * HardwareID, const uint32_t HardwareIDLength ) {    te_CMN_FLAG_QUESTION   is_target;    ts_LIBUTI_SIF_target   v_target;    ts_LIBUTI_SIF_iterator v_titer;    TE_LIBUTI_SIF_status   status;    status = LIBUTI_SIF_init_target_iterator( DecodedHeaderAddress, &v_titer );    v_target.Name.Length = (uint16_t)0;    v_target.hardware_identifier.Length = (uint16_t)0;    is_target = e_CMN_FLAG_NO;    if ( status == E_LIBUTI_SIF_SUCCESS )    {       status = LIBUTI_SIF_get_next_target( &v_target, &v_titer );    }    while( ( status != E_LIBUTI_SIF_FAILURE ) && ( is_target == e_CMN_FLAG_NO ) )    {       is_target = e_CMN_FLAG_YES;       if ( TargetName != NULL )       {          if ( TargetNameLength == (uint32_t)(v_target.Name.Length) )          {             if ( BOOT_LIBMEM_NOTEQUAL(TargetName, v_target.Name.Name, v_target.Name.Length) == e_CMN_FLAG_NO )             {                is_target = e_CMN_FLAG_YES;             }             else             {                is_target = e_CMN_FLAG_NO;             }          }          else          {             is_target = e_CMN_FLAG_NO;          }       }       if ( ( is_target == e_CMN_FLAG_YES ) && ( HardwareID != NULL ) )       {          if ( HardwareIDLength == (uint32_t)(v_target.hardware_identifier.Length) )          {             if ( BOOT_LIBMEM_NOTEQUAL(HardwareID, v_target.hardware_identifier.Name, v_target.hardware_identifier.Length) == e_CMN_FLAG_NO )             {                is_target = e_CMN_FLAG_YES;             }             else             {                is_target = e_CMN_FLAG_NO;             }          }          else          {             is_target = e_CMN_FLAG_NO;          }       }       status = LIBUTI_SIF_get_next_target( &v_target, &v_titer );    }    return ( is_target ); }
