#include "DrvFTM_Private.h"
#include "DrvFTM_Public.h"
#include "Boot_Public.h"
#include "MCP_Types.h"

 void DrvFTM_Start_TSpeed_LS (uint16_t p_mod) {    if (p_mod > (uint16_t) 0)    {       p_mod = p_mod - 1;    }    else    {     }    C_DrvFTM_Controller[DRVFTM_TIMER_0]->S_MOD = (uint16_t) (0.5f * p_mod);    C_DrvFTM_Controller[DRVFTM_TIMER_0]->S_CONTROLS[DRVFTM_CHAN_3].S_CnSC |= ((1UL << DRVFTM_CNSC_ELSA_SHIFT)                                                                             & DRVFTM_CNSC_ELSA_MASK);    C_DrvFTM_Controller[DRVFTM_TIMER_0]->S_CONTROLS[DRVFTM_CHAN_3].S_CnSC |=       ((1UL << DRVFTM_CNSC_MSA_SHIFT) & DRVFTM_CNSC_MSA_MASK);    C_DrvFTM_Controller[DRVFTM_TIMER_0]->S_CONTROLS[DRVFTM_CHAN_3].S_CnV = (uint16_t) (0.5f * p_mod);    C_DrvFTM_Controller[DRVFTM_TIMER_0]->S_SC = 0;    C_DrvFTM_Controller[DRVFTM_TIMER_0]->S_SC |=       ((1UL << DRVFTM_SC_CLKS_SHIFT) & DRVFTM_SC_CLKS_MASK);    C_DrvFTM_Controller[DRVFTM_TIMER_0]->S_SC |=       ((1UL << DRVFTM_SC_PWMEN3_SHIFT) & DRVFTM_SC_PWMEN3_MASK);    C_DrvFTM_Controller[DRVFTM_TIMER_1]->S_SC |=       ((1UL << DRVFTM_SC_TOIE_SHIFT) & DRVFTM_SC_TOIE_MASK);    DrvFTM_Start (1);    C_DrvFTM_Controller[DRVFTM_TIMER_1]->S_CONTROLS[DRVFTM_CHAN_5].S_CnSC |=       ((1UL << DRVFTM_CNSC_ELSA_SHIFT) & DRVFTM_CNSC_ELSA_MASK);    C_DrvFTM_Controller[DRVFTM_TIMER_1]->S_CONTROLS[DRVFTM_CHAN_5].S_CnSC |=       ((1UL << DRVFTM_CNSC_ELSB_SHIFT) & DRVFTM_CNSC_ELSB_MASK);    C_DrvFTM_Controller[DRVFTM_TIMER_1]->S_CONTROLS[DRVFTM_CHAN_5].S_CnSC |=       ((1UL << DRVFTM_CNSC_CHIE_SHIFT) & DRVFTM_CNSC_CHIE_MASK); }
