#include "MCP_Types.h"
#include "DrvLPIT_Private.h"
#include "DrvLPIT_Public.h"

 void DrvLPIT_Reset (void) {    uint32_t timer_id;    uint32_t timer_tif;    timer_tif = DrvLPIT_LPIT_Registers->S_MSR & DRVLPIT_MSR_TIF_MASK;    if ((timer_tif & DRVLPIT_MSR_TIF_0_MASK ) == DRVLPIT_MSR_TIF_0_MASK)    {       timer_id = DRVLPIT_IDENT_0;    }    else    {       if ((timer_tif & DRVLPIT_MSR_TIF_1_MASK ) == DRVLPIT_MSR_TIF_1_MASK)       {          timer_id = DRVLPIT_IDENT_1;       }       else       {          timer_id = DRVLPIT_IDENT_ERROR;       }    }    if (timer_id == DRVLPIT_IDENT_0)    {       DrvLPIT_LPIT_Registers->S_MSR =           (1UL << DRVLPIT_MSR_TIF_0_SHIFT) & DRVLPIT_MSR_TIF_0_MASK;    }    else    {       if (timer_id == DRVLPIT_IDENT_1)       {          DrvLPIT_LPIT_Registers->S_MSR =              (1UL << DRVLPIT_MSR_TIF_1_SHIFT) & DRVLPIT_MSR_TIF_1_MASK;       }       else       {          DrvLPIT_LPIT_Registers->S_MSR =              (1UL << DRVLPIT_MSR_TIF_0_SHIFT) & DRVLPIT_MSR_TIF_0_MASK;          DrvLPIT_LPIT_Registers->S_MSR =              (1UL << DRVLPIT_MSR_TIF_1_SHIFT) & DRVLPIT_MSR_TIF_1_MASK;       }    } }
